<?phpinclude 'IServicesRest.php';class ServicesRest implements IServicesRest{	/**	 * send	 *	 * @param string $user	 * @param string $pass	 * @param string $source	 * @param string $target	 * @param string $text	 * 	 * @return string	 */	function send($user, $pass, $source, $target, $text , $sim)	{		if($user=="beta" && $pass=="putofestival1"){						return $this->sendTM4B($source, $target, $text, $sim);			//return "OK";		}		else{			return "Authentication failed";		}	}		function sendTM4B($source, $target, $text, $sim){						$params="username=n2bolsa&password=n2bolsacrak1&type=broadcast&version=2.1&msg=".urlencode($text)."&to=".urlencode($target)."&from=".urlencode($source)."&sim=".urlencode($sim);				$url = "https://www.tm4b.com/client/api/http.php";   		$ch = curl_init();		curl_setopt($ch, CURLOPT_VERBOSE, TRUE);		curl_setopt($ch, CURLOPT_POST, TRUE);		curl_setopt($ch, CURLOPT_URL, $url);		curl_setopt($ch, CURLOPT_POSTFIELDS, $params);		curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 5);		curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, FALSE); 		curl_setopt($ch, CURLOPT_RETURNTRANSFER, TRUE); //gets output	     		$xmldata = curl_exec($ch);			curl_close($ch);		return $xmldata;	}	}